import React from "react"; // Import React if it's not already imported
import { useDispatch } from "react-redux";
import { remove } from "../redux/Slices/CartSlice";
import { toast } from "react-hot-toast";
import { FcDeleteDatabase } from "react-icons/fc"; // Import the icon here

const CartItem = ({ item, itemIndex }) => {
  const dispatch = useDispatch();

  const removeFromCart = () => {
    dispatch(remove(item.id));
    toast.success("Item Removed");
  };

  return (
    <div className="p-4 border rounded-md shadow-md mb-4">
      <div className="flex items-center">
        <div className="w-16 h-16 mr-4">
          <img src={item.image} alt={item.title} className="w-full h-full object-cover" />
        </div>
        <div>
          <h1 className="text-lg font-semibold">{item.title}</h1>
          <p className="text-gray-600">{item.description}</p>
          <div className="flex items-center">
            <p className="text-lg font-semibold text-green-600">${item.price}</p>
            <div
              className="cursor-pointer ml-2 text-red-600"
              onClick={removeFromCart}
            >
              <FcDeleteDatabase />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItem;
