import React from "react";
import { useSelector } from "react-redux";

const CartSummary = () => {
  const cart = useSelector((state) => state.cart);

  // Calculate the total items and total amount
  const totalItems = cart.reduce((total, item) => total + item.quantity, 0);
  const totalAmount = cart.reduce(
    (total, item) => total + item.quantity * item.price,
    0
  );

  return (
    <div className="bg-gray-100 p-4 rounded-md shadow-md">
      <h2 className="text-2xl font-semibold mb-4">Your Cart Summary</h2>
      <div className="flex flex-col items-center">
        <p className="text-xl mb-2">Total Items: {totalItems}</p>
        <p className="text-xl mb-4">Total Amount: ${totalAmount.toFixed(2)}</p>
        <button className="bg-green-500 hover:bg-green-600 text-white py-2 px-4 rounded-md">
          Checkout Now
        </button>
      </div>
    </div>
  );
};

export default CartSummary;
