import React, { useState, useEffect } from "react";
import Spinner from "../components/Spinner";
import Product from "../components/Product";

const Home = () => {
  const API_URL = "https://fakestoreapi.com/products";
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [sortByPrice, setSortByPrice] = useState("asc");
  const [selectedCategory, setSelectedCategory] = useState("all"); // Default to show all categories
  const [randomlyPickedProduct, setRandomlyPickedProduct] = useState(null);

  async function fetchProductData() {
    setLoading(true);

    try {
      const res = await fetch(API_URL);
      const data = await res.json();

      setPosts(data);
    } catch (error) {
      console.log("Error occurred while fetching data:", error);
      setPosts([]);
    }
    setLoading(false);
  }

  useEffect(() => {
    fetchProductData();
  }, []);

  const sortPostsByPrice = (direction) => {
    const sortedPosts = [...posts];

    if (direction === "asc") {
      sortedPosts.sort((a, b) => a.price - b.price);
    } else if (direction === "desc") {
      sortedPosts.sort((a, b) => b.price - a.price);
    }

    setPosts(sortedPosts);
    setSortByPrice(direction);
  };

  const filterByCategory = (category) => {
    setSelectedCategory(category);
  };

  const filteredPosts =
    selectedCategory === "all"
      ? posts
      : posts.filter((post) => post.category === selectedCategory);

  const handleRandomPick = () => {
    const randomIndex = Math.floor(Math.random() * filteredPosts.length);
    const randomProduct = filteredPosts[randomIndex];
    setRandomlyPickedProduct(randomProduct);
  };

  return (
    <div className="p-4">
      <div className="flex items-center mb-4">
        <div className="mr-4">
          <label className="text-gray-600 font-semibold">Sort by Price:</label>
          <select
            className="w-40 bg-gray-200 hover:bg-gray-300 text-gray-800 py-2 px-4 rounded-md shadow-md"
            value={sortByPrice}
            onChange={(e) => sortPostsByPrice(e.target.value)}
          >
            <option value="asc">Low to High</option>
            <option value="desc">High to Low</option>
          </select>
        </div>

        <div>
          <label className="text-gray-600 font-semibold">
            Filter by Category:
          </label>
          <select
            className="w-40 bg-gray-200 hover:bg-gray-300 text-gray-800 py-2 px-4 rounded-md shadow-md"
            value={selectedCategory}
            onChange={(e) => filterByCategory(e.target.value)}
          >
            <option value="all">All</option>
            <option value="electronics">Electronics</option>
            <option value="jewelery">Jewelery</option>
          </select>
        </div>

        <button
          onClick={handleRandomPick}
          className="ml-4 bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-md shadow-md"
        >
          Random Pick
        </button>
      </div>

      {randomlyPickedProduct && (
        <div className="mb-4">
          <h2 className="text-xl font-semibold">Randomly Picked Product:</h2>
          <Product key={randomlyPickedProduct.id} post={randomlyPickedProduct} />
        </div>
      )}

      {loading ? (
        <Spinner />
      ) : filteredPosts.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-w-6xl mx-auto">
          {filteredPosts.map((post) => (
            <Product key={post.id} post={post} />
          ))}
        </div>
      ) : (
        <div className="flex justify-center items-center">
          <p>No Data Found</p>
        </div>
      )}
    </div>
  );
};

export default Home;
