import { FaShoppingCart, FaBell } from "react-icons/fa"; // Import the FaBell icon for notifications
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { useState } from "react"; // Import useState to manage dropdown visibility

const Navbar = () => {
  const { cart } = useSelector((state) => state);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);

  // Function to toggle notification dropdown
  const toggleNotification = () => {
    setIsNotificationOpen(!isNotificationOpen);
  };

  return (
    <div>
      <nav className="flex justify-between items-center h-20 max-w-6xl mx-auto">
        <NavLink to="/">
          <div className="ml-5">
            <img src="../logo.png" className="h-36" /> {/* Change the height here */}
          </div>
        </NavLink>

        <div className="flex items-center font-medium text-slate-100 mr-5 space-x-6 relative">
          <NavLink to="/">
            <p>Home</p>
          </NavLink>

          <div className="relative">
            <FaShoppingCart className="text-2xl" />

            {cart.length > 0 && (
              <span
                className="absolute -top-1 -right-2 bg-green-600 text-xs w-5 h-5 flex 
                  justify-center items-center animate-bounce rounded-full text-white"
              >
                {cart.length}
              </span>
            )}
          </div>

          <div className="relative">
            <FaBell
              className="text-2xl cursor-pointer"
              onClick={toggleNotification}
            />
            {/* Notification dropdown */}
            {isNotificationOpen && (
              <div className="absolute top-10 right-4 bg-white shadow-md p-2 rounded-md">
                <div className="text-gray-800 font-semibold mb-2">
                  Welcome to notification sections
                </div>
                <ul>
                  <li className="mb-1">
                    Notification 1 Lorem ipsum dolor sit amet.
                  </li>
                  <li className="mb-1">
                    Notification 2 consectetur adipiscing elit.
                  </li>
                  <li className="mb-1">
                    Notification 3 Sed do eiusmod tempor incididunt.
                  </li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
