import { FaShoppingCart, FaBell, FaArrowLeft, FaArrowRight } from "react-icons/fa";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { useState } from "react";

const Navbar = () => {
  const { cart } = useSelector((state) => state);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [notificationIndex, setNotificationIndex] = useState(0);

  const notifications = [
    "Welcome to notifications!",
    "Happy Shopping",
    "Hope you like it",
  ];

  const toggleNotification = () => {
    setIsNotificationOpen(!isNotificationOpen);
  };

  const nextNotification = () => {
    setNotificationIndex((prevIndex) =>
      prevIndex === notifications.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevNotification = () => {
    setNotificationIndex((prevIndex) =>
      prevIndex === 0 ? notifications.length - 1 : prevIndex - 1
    );
  };

  return (
    <div>
      <nav className="flex justify-between items-center h-20 max-w-6xl mx-auto">
        <NavLink to="/">
          <div className="ml-5">
            <img src="../logo.png" className="h-36" alt="Logo" />
          </div>
        </NavLink>

        <div className="flex items-center font-medium text-slate-100 mr-5 space-x-6 relative">
          <NavLink to="/">
            <FaArrowLeft className="text-2xl cursor-pointer" />
          </NavLink>

          <div className="relative">
            <FaShoppingCart className="text-2xl" />
            {cart.length > 0 && (
              <span
                className="absolute -top-1 -right-2 bg-green-600 text-xs w-5 h-5 flex 
                  justify-center items-center animate-bounce rounded-full text-white"
              >
                {cart.length}
              </span>
            )}
          </div>

          <div className="relative">
            <FaBell className="text-2xl cursor-pointer" onClick={toggleNotification} />
            {isNotificationOpen && (
              <div className="absolute top-10 right-4 bg-white shadow-md p-2 rounded-md w-60">
                <div className="text-gray-800 font-semibold mb-2">
                  {notifications[notificationIndex]}
                </div>
                <div className="flex justify-between">
                  <FaArrowLeft className="text-xl cursor-pointer" onClick={prevNotification} />
                  <FaArrowRight className="text-xl cursor-pointer" onClick={nextNotification} />
                </div>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
