import { useState, useEffect } from "react";
import Spinner from "../components/Spinner";
import Product from "../components/Product";

const Home = () => {
  const API_URL = "https://fakestoreapi.com/products";
  const [loading, setLoading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [sortByPrice, setSortByPrice] = useState("asc");

  async function fetchProductData() {
    setLoading(true);

    try {
      const res = await fetch(API_URL);
      const data = await res.json();

      setPosts(data);
    } catch (error) {
      console.log("Error aagya ji");
      setPosts([]);
    }
    setLoading(false);
  }

  useEffect(() => {
    fetchProductData();
  }, []);

  // Function to sort posts by price
  const sortPostsByPrice = (direction) => {
    const sortedPosts = [...posts];

    if (direction === "asc") {
      sortedPosts.sort((a, b) => a.price - b.price);
    } else if (direction === "desc") {
      sortedPosts.sort((a, b) => b.price - a.price);
    }

    setPosts(sortedPosts);
    setSortByPrice(direction);
  };

  return (
    <div>
      <div className="mb-4">
        <button
          className={`bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded-md shadow-md mr-2 ${
            sortByPrice === "asc" ? "bg-blue-600" : ""
          }`}
          onClick={() => sortPostsByPrice("asc")}
        >
          Sort by Price (Low to High)
        </button>
        <button
          className={`bg-red-500 hover:bg-red-600 text-white py-2 px-4 rounded-md shadow-md ${
            sortByPrice === "desc" ? "bg-red-600" : ""
          }`}
          onClick={() => sortPostsByPrice("desc")}
        >
          Sort by Price (High to Low)
        </button>
      </div>
      {loading ? (
        <Spinner />
      ) : posts.length > 0 ? (
        <div className="grid xs:gridcols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 max-w-6xl p-2 mx-auto space-y-10 space-x-5 min-h-[80vh]">
          {posts.map((post) => (
            <Product key={post.id} post={post} />
          ))}
        </div>
      ) : (
        <div className="flex justify-center items-center">
          <p>No Data Found</p>
        </div>
      )}
    </div>
  );
};

export default Home;
