import { FaShoppingCart, FaBell, FaArrowLeft, FaArrowRight } from "react-icons/fa";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { useState } from "react";

const Navbar = () => {
  const { cart } = useSelector((state) => state);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [notificationContent, setNotificationContent] = useState([]);
  const [notificationIndex, setNotificationIndex] = useState(0);

  const toggleNotification = () => {
    setIsNotificationOpen(!isNotificationOpen);
    if (!isNotificationOpen) {
      setNotificationContent(["Welcome to notifications!", "Happy Shopping", "Hope you like it"]);
      setNotificationIndex(0);
    } else {
      setNotificationContent([]);
    }
  };

  const nextNotification = () => {
    if (notificationIndex < notificationContent.length - 1) {
      setNotificationIndex(notificationIndex + 1);
    } else {
      toggleNotification();
    }
  };

  const prevNotification = () => {
    if (notificationIndex > 0) {
      setNotificationIndex(notificationIndex - 1);
    }
  };

  return (
    <div>
      <nav className="flex justify-between items-center h-20 max-w-6xl mx-auto">
        <NavLink to="/">
          <div className="ml-5">
            <img src="../logo.png" className="h-36" />
          </div>
        </NavLink>

        <div className="flex items-center font-medium text-slate-100 mr-5 space-x-6 relative">
          <NavLink to="/">
            <p>Home</p>
          </NavLink>

          <div className="relative">
            <FaShoppingCart className="text-2xl" />

            {cart.length > 0 && (
              <span
                className="absolute -top-1 -right-2 bg-green-600 text-xs w-5 h-5 flex 
                  justify-center items-center animate-bounce rounded-full text-white"
              >
                {cart.length}
              </span>
            )}
          </div>

          <div className="relative">
            <FaBell
              className="text-2xl cursor-pointer"
              onClick={toggleNotification}
            />
            {/* Notification dropdown */}
            {isNotificationOpen && (
              <div className="absolute top-10 right-0 bg-white shadow-md p-2 rounded-md w-60">
                {notificationContent.length > 0 && (
                  <div className="text-gray-800 font-semibold mb-2">
                    {notificationContent[notificationIndex]}
                  </div>
                )}
                {notificationContent.length > 1 && (
                  <div className="flex justify-end">
                    <FaArrowLeft
                      className="text-blue-500 hover:text-blue-700 cursor-pointer"
                      onClick={prevNotification}
                    />
                    <FaArrowRight
                      className="text-blue-500 hover:text-blue-700 cursor-pointer ml-2"
                      onClick={nextNotification}
                    />
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
