import { FaShoppingCart, FaBell } from "react-icons/fa"; // Import the FaBell icon for notifications
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css"; // Import the CSS for styling

const Navbar = () => {
  const { cart } = useSelector((state) => state);

  // Function to show a sample notification
  const showNotification = () => {
    toast.info("This is a notification!");
  };

  return (
    <div>
      <nav className="flex justify-between items-center h-20 max-w-6xl mx-auto">
        <NavLink to="/">
          <div className="ml-5">
            <img src="../logo.png" className="h-36" /> {/* Change the height here */}
          </div>
        </NavLink>

        <div className="flex items-center font-medium text-slate-100 mr-5 space-x-6">
          <NavLink to="/">
            <p>Home</p>
          </NavLink>

          <div className="relative">
            <FaShoppingCart className="text-2xl" />

            {cart.length > 0 && (
              <span
                className="absolute -top-1 -right-2 bg-green-600 text-xs w-5 h-5 flex 
                  justify-center items-center animate-bounce rounded-full text-white"
              >
                {cart.length}
              </span>
            )}
          </div>

          <div className="relative">
            <FaBell
              className="text-2xl cursor-pointer"
              onClick={showNotification}
            />
            {/* Notification dropdown */}
            <div className="absolute top-10 right-4 bg-white shadow-md p-2 rounded-md hidden">
              {/* Replace with your notifications content */}
              <p>Notification 1</p>
              <p>Notification 2</p>
              <p>Notification 3</p>
            </div>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
